#!/usr/bin/python"""Sets the logger to use a console"""import loggingimport colorlogdef setup_console_logger(logger_name, level=logging.INFO):    """    Setup a console based logger    :param logger_name: The logger to attach to    :param level: The minimum level to show. This defaults to ignore DEBUG    :return: N/A    """    logger_name.setLevel(logging.DEBUG)    console_handler = colorlog.StreamHandler()    console_handler.setLevel(level)    formatter = colorlog.ColoredFormatter(        "[%(asctime)s.%(msecs)03d] %(log_color)s[%(levelname)-8s]%(reset)s %(filename)30s:%(lineno)d:<%(threadName)s> \t %(log_color)s%(message)s",        "%Y-%m-%d %H:%M:%S")    console_handler.setFormatter(formatter)    logger_name.addHandler(console_handler)